package ascii;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch1 = 'a';  
		char ch2 = 'b';  
		//casting or converting a charter into int type  
		int ascii1 =  ch1;  
		int ascii2 = (int) ch2; 
		
		System.out.println(ascii1+"......"+ascii2);

	}

}
